INSERT INTO `wp_options` VALUES (148,'_transient_feed_9bbd59226dc36b9b26cd43f15694c5c3','a:4:{s:5:\"child\";a:1:{s:0:\"\";a:1:{s:3:\"rss\";a:1:{i:0;a:6:{s:4:\"data\";s:3:\"\n\n\n\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:7:\"version\";s:3:\"2.0\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:1:{s:7:\"channel\";a:1:{i:0;a:6:{s:4:\"data\";s:52:\"\n	\n	\n	\n	\n	\n	\n	\n	\n	\n\n \n	\n		\n		\n		\n		\n		\n		\n		\n		\n		\n	\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:8:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"WordPress News\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:26:\"https://wordpress.org/news\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:59:\"The latest news about WordPress and the WordPress community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:13:\"lastBuildDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 10 Jan 2023 21:38:49 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"language\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"en-US\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"generator\";a:1:{i:0;a:5:{s:4:\"data\";s:40:\"https://wordpress.org/?v=6.2-alpha-55060\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"image\";a:1:{i:0;a:6:{s:4:\"data\";s:11:\"\n	\n	\n	\n	\n	\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:1:{s:0:\"\";a:5:{s:3:\"url\";a:1:{i:0;a:5:{s:4:\"data\";s:29:\"https://s.w.org/favicon.ico?2\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"WordPress News\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:26:\"https://wordpress.org/news\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"width\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:6:\"height\";a:1:{i:0;a:5:{s:4:\"data\";s:2:\"32\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}}s:4:\"item\";a:10:{i:0;a:6:{s:4:\"data\";s:63:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:43:\"WordPress is Turning 20: Let’s Celebrate!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:74:\"https://wordpress.org/news/2023/01/wordpress-is-turning-20-lets-celebrate/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 10 Jan 2023 21:38:49 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:7:\"General\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:4:\"WP20\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=14155\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:106:\"2023 marks the 20th year of WordPress. Read on to learn about how WordPress is celebrating this milestone.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:11:\"Dan Soschin\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:1476:\"\n<p>2023 marks the 20th year of WordPress. Where would we all be without WordPress? Just think of that! While many technologies, software stacks, and fashion trends have come and gone throughout the past two decades, WordPress has thrived. This is due to the fantastic work and contributions of the WordPress community, comprised of thousands of contributors; and millions of users who have embraced the <a href=\"https://wordpress.org/about/\">four freedoms of WordPress</a> and the mission to democratize publishing.</p>\n\n\n\n<p>Let’s celebrate!</p>\n\n\n\n<p>Throughout the beginning of 2023, leading up to the official anniversary date of WordPress’s launch (May 27, 2003), a number of different events will celebrate this important milestone, reflect on the journey, and look toward the future.</p>\n\n\n\n<p>Please join in!</p>\n\n\n\n<p>Over the next few months, be sure to check WordPress’s official social media accounts along with the <a href=\"https://wp20.wordpress.net/\">official anniversary website</a> for updates on how you can be involved in this exciting celebration by contributing content, collecting cool anniversary swag, and much more. </p>\n\n\n\n<p>Use the hashtag <strong>#WP20</strong> on social media so the community can follow along.</p>\n\n\n\n<p>If you have something planned to celebrate that you would like to be considered for inclusion on the official website, please <a href=\"https://forms.gle/my1yknAJYZ4jFLb79\">use this form to share the details</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14155\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:1;a:6:{s:4:\"data\";s:61:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:48:\"WP Briefing: Episode 46: The WP Bloopers Podcast\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:70:\"https://wordpress.org/news/2022/12/episode-46-the-wp-bloopers-podcast/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Sat, 31 Dec 2022 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:7:\"Podcast\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"wp-briefing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=14123\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:115:\"This episode of the WP Briefing features all the Josepha bloopers our little elves have stored away over the year. \";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2022/12/WP-Briefing-046.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Santana Inniss\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:9636:\"\n<p>This episode of the WP Briefing features all the Josepha bloopers our little elves have stored away over the year. </p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2 class=\"wp-block-heading\">Credits</h2>\n\n\n\n<p>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a><br>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/javiarce/\" data-type=\"URL\" data-id=\"https://profiles.wordpress.org/javiarce/\">Javier Arce</a><br>Production:&nbsp;<a href=\"https://profiles.wordpress.org/santanainniss/\">Santana Inniss</a><br>Song: Fearless First by Kevin MacLeod </p>\n\n\n\n<h2 class=\"wp-block-heading\">Transcript</h2>\n\n\n\n<span id=\"more-14123\"></span>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:00]&nbsp;</strong></p>\n\n\n\n<p>Hello everyone, and welcome to the WordPress Briefing, the podcast where you can normally catch quick explanations of the ideas behind the WordPress open source project with the hope that deeper understanding creates deeper appreciation.</p>\n\n\n\n<p>But on today&#8217;s bonus episode, instead of catching quick explanations, you&#8217;ll catch some quick bloopers.&nbsp;</p>\n\n\n\n<p>The end of the year is a time when many people and many cultures gather together, and whether you observe traditions of light or faith, compassion, or celebration from everyone here at the WordPress Briefing Podcast, we&#8217;re wishing you a happy, festive season and a very happy New Year.</p>\n\n\n\n<p>Sit back, relax, and enjoy some of the laughs and outtakes from recording the WP Briefing over the year.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:01:00]&nbsp;</strong></p>\n\n\n\n<p>Hello everyone, and welcome to the WordPress. This is the thing I&#8217;ve done 25 times, and I know how to do it for reals.</p>\n\n\n\n<p>Welcome to WordPress Briefing, episode 20. Oh no, 7? 27? 26? Episode 27. I know how many things I&#8217;ve done.</p>\n\n\n\n<p>Ooh, neat. This is Josepha recording episode 46 of the WP Bonus Briefings. Not because we&#8217;ve had 46 bonus Briefings, but because this is the 46th one and it is a bonus, it will also have a fancy name. But right now, I&#8217;m just calling it the bonus. It&#8217;s gonna be quick. Here I go.&nbsp;</p>\n\n\n\n<p>Group them into two big buckets, themes, uh, themes and tools. Mmm, I&#8217;m gonna have to redo the whole thing! No! I thought I could save it, and I didn&#8217;t save it. I had a typo in my script, and then I messed it up. I, it said into you big buckets instead of into two big buckets.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:02:00]&nbsp;</strong></p>\n\n\n\n<p>I&#8217;m gonna start over from the target release date because I kind of smeared it all together, um, despite what I intended to do.</p>\n\n\n\n<p>And gives everyone, no. What is this ringing of phones? Oh, I was doing so well. Where was I? Let&#8217;s see if I can just pick it up.</p>\n\n\n\n<p>All righty, live from my closet. It&#8217;s episode 20, the WordPress Briefing, WP Briefing. So I have a title for this, and when I started writing it, I really had every intention of writing it to the title. And then what I wrote doesn&#8217;t fit the title at all, but does really hang together well. And so we&#8217;re gonna have to come up with a new title, but at the moment, it&#8217;s called So Many Ways to WordPress.</p>\n\n\n\n<p>Here in a minute, you will see why it doesn&#8217;t fit. Also, at the end, I feel like I get very, like, angry nerd leader.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:03:00]&nbsp;&nbsp;</strong></p>\n\n\n\n<p>And so I may, I may at the end, give that a second go and see if there&#8217;s a way that I can soften it a little bit, but, I, I don&#8217;t know that I can soften it. I feel very strongly about it. So, maybe I am just an angry nerd leader.</p>\n\n\n\n<p>Oh, okay. I&#8217;ll get us started now that I apparently have filled the room with apologies, not the room, the closet.&nbsp;</p>\n\n\n\n<p>We&#8217;ll figure out something very catchy as a title or as an alternative. Very descriptive, and people will click on it because they must know, but we&#8217;ll figure out the title later.</p>\n\n\n\n<p>@wordpress.org. However, I don&#8217;t know why I decided to do an invitation to email me in the middle of that. I&#8217;m gonna start from the top of that paragraph. I just got too excited by the opportunity to get mail.</p>\n\n\n\n<p>I gotta slow it down. I&#8217;m like the fastest talker, had too much coffee. Okay, slowing it down now.&nbsp;</p>\n\n\n\n<p>Huh? What am I saying? No, no, that&#8217;s what I&#8217;m saying. It&#8217;s fine. I, I can do this.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:04:00]</strong></p>\n\n\n\n<p>Hold on. Oww. Sorry. I was adjusting my microphone, and then it fell down. I happened to be holding it at the time, so it didn&#8217;t, like, slam down, I think, and hurt your ears and so I apologize. Good thing I stopped so it didn&#8217;t just, like, slam down in the middle of a recording.</p>\n\n\n\n<p>That&#8217;s all right. I&#8217;m gonna give myself that win, even though it&#8217;s a hollow one. All right. Trying again. Starting right there, at now since.</p>\n\n\n\n<p>This year, it starts on October 18th, 2001. That&#8217;s the year? No, 2021. That&#8217;s the year. Oh man. I&#8217;m doing such a great job of this.</p>\n\n\n\n<p>Um, I&#8217;m recording this slightly before, um, you&#8217;re hearing it? What, how am I gonna start this? Hold on. I don&#8217;t know how to start this. All right. I&#8217;m, I can do it.</p>\n\n\n\n<p>Oh, I&#8217;m so glad I remembered. We had guests that could have been so embarrassing.</p>\n\n\n\n<p>Now for me, the trade-offs work well. How many times can I say now?</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:05:00]&nbsp;</strong></p>\n\n\n\n<p>Do I just start every sentence with now now? Is this just how I do things? Uh, now, now, now, now. I&#8217;m gonna start all over again because I&#8217;m in my head about the words in my mouth now. So.</p>\n\n\n\n<p>In some near timeframe, some near timeframe. This is not a thing that people say, Dustin, I&#8217;m sorry. That&#8217;s not a thing people say. I&#8217;m just gonna retry that one sentence to sound like I speak with other human beings sometimes.</p>\n\n\n\n<p>Today is the start of… I can do these things.</p>\n\n\n\n<p>This was a terrible ending. I need to just finish that last part. I&#8217;m gonna redo the part where I started with my name and not the name of the podcast. Um, and we&#8217;ll do that.</p>\n\n\n\n<p>And if you&#8217;re supporting or building anything to hand off to clients, you know that timely, easy to ship changes on a site are considered a vital part of any overarching brand and marketing strategy. Wow. It&#8217;s like, I don&#8217;t know what words are right there.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:06:00]&nbsp;</strong></p>\n\n\n\n<p>I tripped over my own tongue a lot. I&#8217;m gonna sit, I&#8217;m gonna do that paragraph again because I didn&#8217;t do a very good job of it.</p>\n\n\n\n<p>I&#8217;ll do a better job.</p>\n\n\n\n<p>I literally digress, and now I don&#8217;t know. I am in my thing. What was I saying? Oh, there we go.&nbsp;</p>\n\n\n\n<p>Topher DeRosia, who founded Word not WordPress. Holy moly. That was a, I knew I was gonna say that, and I was like, don&#8217;t say that when you actually get around to saying this, but here I am, and I did it. Even though I knew I was gonna do it and I told myself not to. Doing it again. Right from there.</p>\n\n\n\n<p>Not which audiench segment. Oh man. Audiench is not a word, folks. I was on a roll. I&#8217;m gonna start right from the primary thing.</p>\n\n\n\n<p>I don&#8217;t even remember how I started this podcast. What is the last thing I said? I said, here we go. All right.&nbsp;</p>\n\n\n\n<p>Kind of covered some interesting ground, and so, oh no, this is not where I&#8217;m gonna start it. I know exactly where I&#8217;m gonna start it. Okay. I&#8217;m really ready now. Here we go.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:07:00]&nbsp;</strong></p>\n\n\n\n<p>I suddenly, I&#8217;m gonna pause right here because I suddenly got really worried that I didn&#8217;t actually hit record. Oh my gosh. I did. Woo. I&#8217;m all over the place. Okay. We&#8217;ll now continue. Wait, did I? Oh my goodness. I did, super sorry.</p>\n\n\n\n<p>Of the WordPress Briefing. I&#8217;m gonna do some singing in the middle of some talking, but I keep trying to talk myself out of the singing, so I&#8217;m gonna go ahead and do the singing, and then I&#8217;ll do the talking before I talk myself out of the singing. Here I go, probably.</p>\n\n\n\n<p>I added a word. That was so good. I&#8217;m gonna start again. I&#8217;m gonna get some water, and then I&#8217;m gonna start again. Not again. Again. Just from the ‘and finally.’</p>\n\n\n\n<p>I don&#8217;t know how I finish my show. Y&#8217;all, I do this literally every week. I never know how to finish my show. Here we go.</p>\n\n\n\n<p>I don&#8217;t know why I shouted at you from the other side of the tiny closet. I apologize. I&#8217;m gonna start again from ‘and finally.’</p>\n\n\n\n<p>Tada we did it.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:08:00]&nbsp;</strong></p>\n\n\n\n<p>Ha. I hate it. I hate the whole podcast. It&#8217;s gonna be fine.&nbsp;</p>\n\n\n\n<p>Done. Nailed it.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:00]&nbsp;</strong></p>\n\n\n\n<p>With that, I&#8217;m your host, Josepha Haden Chomphosy. Merry Christmas from me. Happy holidays to you, and we&#8217;ll see you again in the new year.</p>\n\n\n\n<p>Done.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14123\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:2;a:6:{s:4:\"data\";s:61:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:54:\"WP Briefing: Episode 45: State of the Word Reflections\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:76:\"https://wordpress.org/news/2022/12/episode-45-state-of-the-word-reflections/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 22 Dec 2022 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:7:\"Podcast\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"wp-briefing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=14070\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:91:\"Josepha reflects on this year\'s State of the Word address here on the WP Briefing podcast. \";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2022/12/WP-Briefing-045.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Santana Inniss\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:14564:\"\n<p>In the forty-fifth episode of the WordPress Briefing, WordPress Executive Director Josepha Haden Chomphosy discusses highlights from this year&#8217;s State of the Word address. </p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2 class=\"wp-block-heading\">Credits</h2>\n\n\n\n<p>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a><br>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/javiarce/\" data-type=\"URL\" data-id=\"https://profiles.wordpress.org/javiarce/\">Javier Arce</a><br>Production:&nbsp;<a href=\"https://profiles.wordpress.org/santanainniss/\">Santana Inniss</a><br>Song: Fearless First by Kevin MacLeod </p>\n\n\n\n<h2 class=\"wp-block-heading\">References</h2>\n\n\n\n<p><a href=\"https://learn.wordpress.org/\">LearnWP</a><br><a href=\"https://developer.wordpress.org/playground/demo/\">WordPress Playground</a><br><a href=\"https://wordpress.org/news/2022/12/state-of-the-word-2022-recap/\">ICYMI: State of the Word Recap</a><br><a href=\"https://wordpress.org/news/2022/12/2022-wordpress-survey/\" data-type=\"URL\" data-id=\"https://wordpress.org/news/2022/12/2022-wordpress-survey/\">Take the 2022 WordPress Survey!</a><br><a href=\"https://make.wordpress.org/training/2022/07/11/exploring-wordpress-certifications/\" data-type=\"URL\" data-id=\"https://make.wordpress.org/training/2022/07/11/exploring-wordpress-certifications/\">Exploring WordPress Certifications</a><br><a href=\"https://communitysummit.wordcamp.org/2023/\" data-type=\"URL\" data-id=\"https://communitysummit.wordcamp.org/2023/\">Community Summit WordCamp Site</a><br><a href=\"https://make.wordpress.org/community/2022/12/08/suggest-topics-for-the-2023-wordpress-community-summit/\">Submit Topics for the 2023 Community Summit</a><br><a href=\"https://wp20.wordpress.net/\">20th Anniversary&#8211; Stay Tuned for Updates</a><br><a href=\"https://wordpress.org/news/2022/11/introducing-twenty-twenty-three/\">Check Out Style Variations and the 2023 Theme</a></p>\n\n\n\n<h2 class=\"wp-block-heading\">Transcript</h2>\n\n\n\n<span id=\"more-14070\"></span>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:00]&nbsp;</strong></p>\n\n\n\n<p>Hello, everyone, and welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks. I&#8217;m your host, Josepha Haden Chomphosy. Here we go.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:39] </strong></p>\n\n\n\n<p>Last week, WordPress hosted its annual State of the Word. As usual, this was delivered by our project co-founder Matt Mullenweg and represented a year-long labor of love from the WordPress community as a whole. There are many things I love about State of the Word, but consistently the thing I love the most is being able to shine spotlights on the great work of our global network of contributors.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:01:02]&nbsp;</strong></p>\n\n\n\n<p>Since that presentation goes by at the speed of light, I wanted to highlight a few things as well. First things first, I wanted to highlight that we had nearly 1,400 contributors, and by nearly, I mean just one too few. We had 1,399 contributors. So that is a big deal in general, but it&#8217;s an especially big deal to me because that&#8217;s before we start looking at any contributions that aren&#8217;t specifically tied to a release.&nbsp;</p>\n\n\n\n<p>You may be wondering what those non-release contributions are. An incomplete list of those contributions would include organizing WordPress events, training others how to use WordPress, the myriad podcasts, articles, and newsletters that make up the WordPress media community, and any participant in a call for testing. Not to mention the unglamorous ways to contribute, like reviewing themes or reviewing plugins.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:01:58]&nbsp;</strong></p>\n\n\n\n<p>Things like patching security vulnerabilities and the bazillion things that Meta does to make sure that our community has all the tools that it needs to function. So I want to echo, once again, the huge, huge thanks that Matt already shared in State of the Word, and thank all of you for showing up for our project and for each other this way.</p>\n\n\n\n<p>The next thing I wanted to be sure to highlight was LearnWP. It was briefly noted that 12,000 learners had found their way to courses on learn.wordpress.org, and then during the Q&amp;A, there was a related question about certifications in WordPress.&nbsp;</p>\n\n\n\n<p>The need for certifications has been a regular topic in our project, and I mentioned that there are two different ongoing discussions at the moment. One of those discussions is happening directly on the make.wordpress.org/training site, so I&#8217;ll share a link in the show notes for that.</p>\n\n\n\n<p>But I&#8217;ve also been personally chatting on and off with Training team reps and other members of the community about what makes that so hard. In case you have not heard my whole spiel about what makes it difficult, it&#8217;s the logistics and our speed of iteration, and public perception.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:03:05] </strong></p>\n\n\n\n<p>So not exactly a small set of hurdles. I&#8217;ll be doing a more complete post on this in the New Year so that we can get some solid documentation of the state of things and not let it be lost forever in this podcast. But I do know that it is something that we are very interested in as a community and something that, historically, I have really been resistant to.</p>\n\n\n\n<p>Not because I think it&#8217;s a bad idea, but because as someone who&#8217;s looking out for our operations side of things and our logistics side of things, it is not clear how we&#8217;re gonna get that done. Like I said, in the New Year, keep an eye out for a big, big post that takes a look at the benefits versus the costs and everything that we can do to help make those match each other a bit better.</p>\n\n\n\n<p>And then the last thing I wanted to highlight was the WordPress Playground. Okay, so this was the last thing that Matt mentioned, and I want to be sure that it&#8217;s clear what&#8217;s going on with this project because when I first heard about it, I very nearly lept from my chair!&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:04:03]&nbsp;</strong></p>\n\n\n\n<p>It was such a remarkably big deal. Okay, so the WordPress Playground uses technological magic called ‘web assembly.’ I don&#8217;t know what it is, but it&#8217;s magic. And when I say magic, I mean that this tool makes it possible to run WordPress, an instance of WordPress, including a theme and a handful of plug-ins entirely inside your browser as a logged-in admin.</p>\n\n\n\n<p>You don&#8217;t need a server. You don&#8217;t need to select a host. You don&#8217;t need to download anything at all. You don&#8217;t need to know what your domain&#8217;s going to be. You simply select the theme you want to test. Add some dummy content and see how all of the posts and pages function as though we&#8217;re a real live WordPress site running on your favorite top-tier host.</p>\n\n\n\n<p>Then when you close the tab, it&#8217;s gone forever. Poof. Just like that. Now, this is a brand new project. It&#8217;s brand new to us and has a long way to go. So if working on that sounds cool, stop by the Meta Playground channel in the Making WordPress Slack.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:05:09]&nbsp;</strong></p>\n\n\n\n<p>But this, in my mind, changes the way that we stage sites.</p>\n\n\n\n<p>It could change the way we determine whether a theme or plugin is right for us. And arguably, it can become a stress-free way to introduce new or undecided users to WordPress’s admin area so that they can tell what they&#8217;re getting into. So when I say that this is a mind-blowing thing, and when I say that it is powered by magic, like it is astounding, it is astounding.</p>\n\n\n\n<p>And the applications for our users as a whole, I think, are untapped yet, and potentially even the applications for our learners and future learners of WordPress– equally untapped. I&#8217;m very excited to see what we can do with this project in the future. So stop by the Meta channel. Stop by Meta Playground.</p>\n\n\n\n<p>See what&#8217;s going on over there. We would love to have you.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:06:00]&nbsp;</strong></p>\n\n\n\n<p>So those are my highlights of the day for State of the Word. Like I said, there are a few things I want to do more of a deep dive on in the text, so keep an eye out on make.wordpress.org/projects for most of those. But right now, let&#8217;s make some time for the small list of big things.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:06:17]&nbsp;</strong></p>\n\n\n\n<p>Today I actually have kind of like a big list of big things. But I pretended it was small, so you didn&#8217;t turn off the podcast. So the first thing that I have is that in case you missed State of the Word, if you didn&#8217;t have a Watch Party to go to, or you didn&#8217;t know it was happening and so you didn&#8217;t really tune in at the time, I&#8217;m going to drop in a link of the recording.</p>\n\n\n\n<p>It&#8217;s gonna probably start right when everything gets going. And so you shouldn&#8217;t have to scrub through anything. If you end up on one of the recordings that includes like the whole live stream, there is jazz for the first 30 minutes, and just, you know, skip through that.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:07:00] </strong></p>\n\n\n\n<p>The second thing on my big list of big things is our annual community survey. So Matt mentioned this in State of the Word, and he pointed out that one of the things that makes WordPress and open source in general so effective is that we have a way to communicate with people who are using our software and we make every effort to be responsive to it.</p>\n\n\n\n<p>So the annual survey that we send out, it used to be quite big, and we&#8217;ve cut it down to 20 questions. If you want, you can think of it as like a census, so have your type of work and how long you&#8217;ve been working in WordPress, and what you wish to do with WordPress– have all those things be counted so we have a good idea of the type of person who&#8217;s currently using WordPress, and we can account for your needs and wants.</p>\n\n\n\n<p>But also, if you want to think of it more as an opportunity to share the things that were especially useful for you in the project this year or especially valuable for you as a contributor, this is also an excellent place to do that.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:08:01]&nbsp;</strong></p>\n\n\n\n<p>There&#8217;s a QR code running around on the internet somewhere, but I&#8217;ll also put a link in the show notes. If you do not know where the show notes are, by the way, they are at wordpress.org/news/podcast, and you&#8217;ll be able to get to the survey.</p>\n\n\n\n<p>The third thing on my big list of big things is that next year we&#8217;re hosting a community summit. So if you&#8217;ve never been to a community summit, Matt mentioned that it is an opportunity for the best and most prolific contributors that we have to show up and discuss the things that are the biggest problems for the WordPress project right now.</p>\n\n\n\n<p>But we also want to make sure that we are making space for the voices that we know that we are missing from the community as well as contributors who look like they are probably excellent future stewards of this open source project that we are taking care of together. And so there is a whole website for that.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:08:55]&nbsp;</strong></p>\n\n\n\n<p>I believe it&#8217;s communitysummit.wordcamp.org. Right now, there is a form up asking for topics that you want to be able to discuss while we are there, but it&#8217;s taking place, if I recall correctly, on August 22nd and 23rd of 2023.</p>\n\n\n\n<p>Number four on my big list of big things is that next year is WordPress&#8217;s 20th anniversary. So on May 27th of next year, WordPress will officially be 20 years old. So on our 10th birthday, anniversary rather, and our 15th anniversary, we pulled together some parties all across the world.&nbsp;</p>\n\n\n\n<p>We had some images, some logos, and things that were specific to the celebration that we printed into stickers and that folks put on, like, mugs and backpacks and cakes and stuff. So if you want to learn more about that, keep an eye out in the community channel in making WordPress Slack. They will keep you posted on how to one, find any of those logos and designs so that your local community can join in the celebrations.<br></p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:10:03]&nbsp;</strong></p>\n\n\n\n<p>But they will also help you learn how to have any sort of WordPress celebration party that we&#8217;re doing there in May of 2023.&nbsp;</p>\n\n\n\n<p>And then the final thing on my big list of big things, it was mentioned that on the 2023 theme that was shipped with a bunch of style variations and there was this really, I think, excellent illustrative video that <a href=\"https://profiles.wordpress.org/richtabor/\">Rich Tabor</a> put together for us that shows that you can switch through style variations on a single theme and have a site that looks totally different.</p>\n\n\n\n<p>Now, that feels like that&#8217;s just a thing that should always have been in WordPress, but it is new this year. And so, if you have not yet had a chance to look at the 2023 theme, it is the default theme that shipped with 6.1. And so, if you have it on your website and just haven&#8217;t had a look at it yet, I encourage you to do that.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:11:00]</strong></p>\n\n\n\n<p>It&#8217;s a really interesting implementation that makes a single theme potentially look like an infinite number of other themes, and those style variations can be specific to the theme or can just kind of be around and about in the patterns that are also available in Core.&nbsp;</p>\n\n\n\n<p>Give that a look. I think it&#8217;s super worthwhile.</p>\n\n\n\n<p>And that, my friends, is your big list of big things. Thank you for tuning in today for the WordPress Briefing. I&#8217;m your host, Josepha Haden Chomphosy, and I&#8217;ll see you again in the New Year.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14070\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:3;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:40:\"The Month in WordPress – November 2022\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:72:\"https://wordpress.org/news/2022/12/the-month-in-wordpress-november-2022/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 20 Dec 2022 12:05:04 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:18:\"Month in WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:18:\"month in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=14124\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:317:\"WordPress enthusiasts tuned in last week for the State of the Word address to celebrate the project\'s yearly accomplishments and explore what 2023 holds. But that’s not the only exciting update from the past month. New proposals and ideas are already emerging with an eye on the year ahead—let’s dive into them!\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"rmartinezduque\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:13931:\"\n<p>WordPress enthusiasts tuned in last week for the State of the Word address to celebrate the project&#8217;s yearly accomplishments and explore what 2023 holds. But that’s not the only exciting update from the past month. New proposals and ideas are already emerging with an eye on the year ahead—let’s dive into them!</p>\n\n\n\n<hr class=\"wp-block-separator has-text-color has-light-grey-color has-alpha-channel-opacity has-light-grey-background-color has-background\" />\n\n\n\n<h2 class=\"wp-block-heading\">Highlights from State of the Word 2022</h2>\n\n\n\n<p>WordPress co-founder <a href=\"https://profiles.wordpress.org/matt/\">Matt Mullenweg</a> delivered the annual State of the Word address on <strong>December 15, 2022</strong>, before a live audience in New York City. Most attendees joined the event via livestream or one of the 33 watch parties held across 11 countries.</p>\n\n\n\n<p><a href=\"https://profiles.wordpress.org/chanthaboune/\">Josepha Haden Chomphosy</a>, Executive Director of WordPress, kicked off this year&#8217;s event with an introduction to the <strong>Four Freedoms of open source</strong> and the importance of WordPress in ensuring “a free, open and interconnected web for the future.”</p>\n\n\n\n<p>Similar to past State of the Word events, Matt reflected on the project’s achievements over the past year, including Gutenberg’s adoption beyond WordPress, the steady progress in advancing the site editing experience, and the return to in-person events.<em> </em>In addition, he took the opportunity to remind everyone of the <a href=\"https://communitysummit.wordcamp.org/2023/\">2023 Community Summit</a> and the <a href=\"https://wp20.wordpress.net/\">20th anniversary of WordPress</a> coming up next year.</p>\n\n\n\n<p><strong>Ahead of 2023</strong>, Matt announced <a href=\"https://make.wordpress.org/plugins/2022/12/16/plugins-themes-categorization/\">new taxonomies</a> in the WordPress.org theme and plugin directories to help users identify the extensions that best fit their needs and plans for Phase 3 of Gutenberg—Collaboration—among other notable updates.</p>\n\n\n\n<p>People who watched the State of the Word enjoyed a demo of <a href=\"https://developer.wordpress.org/playground/\">WordPress Playground</a>, an experimental project to explore, experiment, and build apps with a WordPress instance that runs entirely in the browser.</p>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Missed the event? <a href=\"https://wordpress.org/news/2022/12/state-of-the-word-2022-recap/\">Read the recap</a> or <a href=\"https://wordpress.tv/2022/12/16/matt-mullenweg-state-of-the-word-2022/\">watch the State of the Word recording</a> and <a href=\"https://wordpress.tv/2022/12/16/matt-mullenweg-state-of-the-word-2022-qa/\">Q&amp;A session</a> on WordPress.tv.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">The 2022 WordPress Survey is open</h2>\n\n\n\n<p>The annual WordPress survey helps project leadership and those who build WordPress understand more about the contributor experience, how the software is used, and by whom.</p>\n\n\n\n<p>This year’s survey will remain <strong>open through the end of 2022</strong> and is available in English, French, German, Italian, Japanese, Russian, and Spanish.</p>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p><a href=\"https://wordpress.org/news/2022/12/2022-wordpress-survey/\">Take the 2022 WordPress Survey</a> to help make an impact on the project.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">What’s new in Gutenberg</h2>\n\n\n\n<p>Two new versions of Gutenberg have shipped in the last month:</p>\n\n\n\n<ul>\n<li><a href=\"https://make.wordpress.org/core/2022/11/23/whats-new-in-gutenberg-14-6-23-november/\"><strong>Gutenberg 14.6</strong></a>, released on November 23, 2022, came with many refinements to core blocks. Notable highlights include a variation picker that allows users to choose a desired layout when a Group block is inserted on a page, a new list view for editing the Navigation block, and a keyboard shortcut to transform paragraph blocks into headings.</li>\n\n\n\n<li><a href=\"https://make.wordpress.org/core/2022/12/09/whats-new-in-gutenberg-14-7-7-december/\"><strong>Gutenberg 14.7</strong></a>, released on December 7, 2022, introduced an experimental tabbed sidebar, colors to help identify some block types in list view, and improvements to the Page List block to make it easier to manage page links in the content.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Follow the “<a href=\"https://make.wordpress.org/core/tag/gutenberg-new/\">What’s new in Gutenberg</a>” posts to stay on top of the latest enhancements.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">Team updates: Introducing the block editor in the support forums, a revamped Showcase page, and more</h2>\n\n\n\n<ul>\n<li>The Support and Meta Teams are bringing <a href=\"https://make.wordpress.org/support/2022/12/introducing-the-block-editor-in-the-support-forums/\">the block editor experience into the WordPress.org support forums</a>.</li>\n\n\n\n<li>WordPress.org’s redesign continues with a <a href=\"https://make.wordpress.org/meta/2022/12/14/showcase-visual-refresh/\">refreshed Showcase page</a>.</li>\n\n\n\n<li>A <a href=\"https://make.wordpress.org/community/2022/12/05/proposed-2023-global-community-sponsorship-program/\">proposal for the 2023 Global Community Sponsorship Program</a> has been published.</li>\n\n\n\n<li>WordPress is accessible to more people thanks to 19,357 contributors who translated more than eight million strings this year. Learn more about the <a href=\"https://make.wordpress.org/polyglots/2022/12/12/reflecting-on-2022-in-polyglots/\">Polyglots Team&#8217;s achievements in 2022</a>.</li>\n\n\n\n<li>Speaking of accomplishments, supporters of the Global Meetup Reactivation project <a href=\"https://make.wordpress.org/community/2022/12/09/december-update-global-meetup-reactivation-project/\">helped reactivated 126 meetup groups</a> in 2022! There are also some discussions on <a href=\"https://make.wordpress.org/community/2022/12/15/idea-creating-a-translation-events-kit-for-meetups/\">how these efforts can support the Polyglots Outreach Effort project</a>, and vice versa.</li>\n\n\n\n<li>The Community Team met with members of the Meetup.com team to help report some feedback and issues with an accessibility overlay that they ultimately <a href=\"https://make.wordpress.org/community/2022/11/23/meetup-com-accessibility-overlay-update/\">agreed to remove</a>.</li>\n\n\n\n<li>The Training Team highlighted <a href=\"https://make.wordpress.org/training/2022/12/05/learn-wordpress-site-updates-december-2022/\">the latest updates</a> that have been made to <a href=\"https://learn.wordpress.org/\">Learn WordPress</a>. In addition, <a href=\"https://learn.wordpress.org/course/a-developers-guide-to-block-themes-part-1/\">part 1</a> and <a href=\"https://learn.wordpress.org/course/a-developers-guide-to-block-themes-part-2/\">part 2</a> of the Developers Guide to Block Themes are now available on the platform.</li>\n\n\n\n<li>The <a href=\"https://make.wordpress.org/themes/2022/12/05/introducing-new-themes-team-representatives-for-the-2023-edition/\">Themes</a> and <a href=\"https://make.wordpress.org/polyglots/2022/11/08/call-for-polyglots-team-representatives-2/#comment-295831\">Polyglots</a> Teams announced their team representatives for 2023.</li>\n\n\n\n<li>The <a href=\"https://make.wordpress.org/core/2022/11/30/now-available-wordpress-3-7-4-0-final-releases/\">final releases for WordPress 3.7-4.0</a> were made available on November 30, 2022, with a notice that they are no longer receiving security updates. WordPress 4.1 and later will continue to receive such updates.</li>\n\n\n\n<li>Fresh off the press, the <a href=\"https://make.wordpress.org/polyglots/2022/12/20/polyglots-monthly-newsletter-december-2022/\">December 2022 edition of the Polyglots monthly newsletter</a> is available for reading.</li>\n\n\n\n<li>The latest edition of People of WordPress features <a href=\"https://wordpress.org/news/2022/11/people-of-wordpress-huanyi-chuang/\">Huanyi Chuang</a>, a front end developer from Taiwan.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Curious about why WordPress has so many releases? <a href=\"https://wordpress.org/news/2022/11/episode-44-minors-majors-and-why-we-have-so-many-releases/\">Tune in to Episode 44 of WP Briefing</a> to learn about the role of major and minor releases in the project.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">Feedback &amp; testing requests</h2>\n\n\n\n<ul>\n<li>Help efforts to wrap up Gutenberg Phase 2 by <a href=\"https://make.wordpress.org/test/2022/12/14/fse-program-testing-call-19-running-through-refinements/\">testing the Site Editor’s latest updates</a>. Leave your feedback by January 6, 2023.</li>\n\n\n\n<li>The Performance Team shared a <a href=\"https://make.wordpress.org/core/2022/12/09/enhancing-the-scripts-api-with-a-loading-strategy/\">new proposal for enhancing the Scripts API with a loading strategy</a>.</li>\n\n\n\n<li>The Full Site Editing (FSE) Outreach Program is <a href=\"https://make.wordpress.org/test/2022/12/07/fse-program-seeking-volunteers-to-use-replay-io-for-capturing-bugs/\">looking for volunteers to use Replay.io for capturing bugs</a>. Express your interest by December 28, 2022.</li>\n\n\n\n<li>Version 21.4 of the WordPress mobile app for <a href=\"https://make.wordpress.org/mobile/2022/12/20/call-for-testing-wordpress-for-android-21-4/\">Android</a> and <a href=\"https://make.wordpress.org/mobile/2022/12/19/call-for-testing-wordpress-for-ios-21-4/\">iOS</a> is available for testing.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>The Community Team is calling on WordPress contributor teams to <a href=\"https://make.wordpress.org/community/2022/12/08/suggest-topics-for-the-2023-wordpress-community-summit/\">suggest topics for the 2023 Community Summit</a> by January 16, 2023.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">WordPress events updates</h2>\n\n\n\n<ul>\n<li>The #WPDiversity working group organized <a href=\"https://make.wordpress.org/community/2022/12/17/report-wpdiversity-inclusion-and-diverse-speaker-workshops-october-and-november-2022/\">several workshops</a> during the past few months. Among other highlights, attendees of the Speaker Workshop for Women Voices in Latin America reported a 52% increase in self-confidence to speak in public. <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/event-formats/diversity-speaker-training-workshop/#upcoming-wpdiversity-workshops\">Stay tuned for the next events</a>.</li>\n\n\n\n<li>The WordCamp Europe 2023 organizing team shared <a href=\"https://europe.wordcamp.org/2023/wceu-2023-content-vision/\">their content vision</a> for next year&#8217;s flagship event in Athens, Greece.</li>\n\n\n\n<li><a href=\"https://asia.wordcamp.org/2023/\">WordCamp Asia 2023</a> is just a few months away, scheduled for February 17-19, 2023, in Bangkok, Thailand. Organizers have announced the first recipient of the WordCamp Asia Diversity Scholarship, <a href=\"https://asia.wordcamp.org/2023/diversity-scholarship-recipient/\">Awais Arfan</a>.</li>\n\n\n\n<li>Three more WordCamps are happening in the next few months:\n<ul>\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ea-1f1f8.png\" alt=\"🇪🇸\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://zaragoza.wordcamp.org/2023/\">WordCamp Zaragoza</a>, Spain on January 20-21, 2023</li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1fa-1f1f8.png\" alt=\"🇺🇸\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://birmingham.wordcamp.org/2023/\">WordCamp Birmingham, Alabama</a>, USA on February 4-5, 2023</li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1f5-1f1ed.png\" alt=\"🇵🇭\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://cebu.wordcamp.org/2023/\">WordCamp Cebu</a>, Philippines on February 11, 2023</li>\n</ul>\n</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>WordCamp Europe 2023 is calling for <a href=\"https://europe.wordcamp.org/2023/the-call-for-sponsors-is-now-open/\">sponsors</a> and <a href=\"https://europe.wordcamp.org/2023/call-for-speakers-is-open-now/\">speakers</a>.</p>\n</blockquote>\n\n\n\n<div style=\"height:5px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<hr class=\"wp-block-separator has-text-color has-light-grey-color has-alpha-channel-opacity has-light-grey-background-color has-background\" />\n\n\n\n<p><strong><em><strong><em><strong><em>Have a story we should include in the next issue of The Month in WordPress? <strong><em>Fill out </em></strong><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\"><strong><em>this quick form</em></strong></a><strong><em> to let us know.</em></strong></em></strong></em></strong></em></strong></p>\n\n\n\n<p><em>The following folks contributed to this edition of The Month in WordPress: <a href=\'https://profiles.wordpress.org/cbringmann/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>cbringmann</a>, <a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>, <a href=\'https://profiles.wordpress.org/sereedmedia/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>sereedmedia</a>, and <a href=\'https://profiles.wordpress.org/rmartinezduque/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>rmartinezduque</a>.</em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14124\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:4;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"State of the Word 2022: A Celebration of the Four Freedoms of Open Source\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:64:\"https://wordpress.org/news/2022/12/state-of-the-word-2022-recap/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Fri, 16 Dec 2022 22:11:15 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:17:\"state of the word\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=14110\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:355:\"WordPress belongs to all of us, but really we’re taking care of it for the next generation.” Matt Mullenweg A small audience of WordPress contributors, developers, and extenders gathered on December 15 for the annual State of the Word keynote from WordPress co-founder Matt Mullenweg. Those who could not join in person joined via livestream [&#8230;]\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:5677:\"\n<blockquote class=\"wp-block-quote\">\n<p>WordPress belongs to all of us, but really we’re taking care of it for the next generation.” </p>\n<cite>Matt Mullenweg</cite></blockquote>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<iframe class=\"youtube-player\" width=\"600\" height=\"338\" src=\"https://www.youtube.com/embed/VeigCZuxnfY?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\" allowfullscreen=\"true\" style=\"border:0;\" sandbox=\"allow-scripts allow-same-origin allow-popups allow-presentation\"></iframe>\n</div></figure>\n\n\n\n<p>A small audience of WordPress contributors, developers, and extenders gathered on December 15 for the annual State of the Word keynote from WordPress co-founder <a href=\"https://profiles.wordpress.org/matt/\">Matt Mullenweg</a>. Those who could not join in person joined via livestream or one of 33 watch parties held across 11 countries, with more than 500 RSVPs.</p>\n\n\n\n<figure class=\"wp-block-image size-large\"><img decoding=\"async\" loading=\"lazy\" width=\"1024\" height=\"576\" src=\"https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?resize=1024%2C576&#038;ssl=1\" alt=\"The four freedoms of open source: run the program, study the change, redistribute, and distribute your copies.\" class=\"wp-image-14111\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?resize=1024%2C576&amp;ssl=1 1024w, https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?resize=300%2C169&amp;ssl=1 300w, https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?resize=768%2C432&amp;ssl=1 768w, https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?resize=1536%2C864&amp;ssl=1 1536w, https://i0.wp.com/wordpress.org/news/files/2022/12/the-four-freedoms.jpeg?w=1920&amp;ssl=1 1920w\" sizes=\"(max-width: 1000px) 100vw, 1000px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>Executive Director, <a href=\"https://profiles.wordpress.org/chanthaboune/\">Josepha Haden Chomphosy</a>, introduced the event with a reminder of why so many of those gathered choose WordPress—the Four Freedoms of open source. As Haden Chomphosy noted, open source is an idea that can change our generation, and WordPress is one of the most consistent and impactful stewards of those freedoms.</p>\n\n\n\n<p>As with past State of the Word events, Matt reflected on the year’s accomplishments, learnings, and aspirations as the project moves into 2023. From Gutenberg concluding its second phase of site editing in preparation for phase three—Collaborative Workflows, to the reactivation of meetups and global WordCamps, to the introduction of a new theme and plugin taxonomy, to musings on the potential of machine learning, WordPress enters its <a href=\"https://wp20.wordpress.net/\">20th year</a> continuing to define bleeding edge technology in thanks to the ecosystem’s vibrant community.&nbsp;</p>\n\n\n\n<p>The one-hour multimedia presentation was followed by an interactive <a href=\"https://wordpress.tv/2022/12/16/matt-mullenweg-state-of-the-word-2022-qa/\">question and answer</a> session where Matt fielded questions from the livestream and studio audience. All questions will be responded to in a follow-up post on <a rel=\"noreferrer noopener\" href=\"https://make.wordpress.org/project\" target=\"_blank\">Make.WordPress.org/project</a>.&nbsp;</p>\n\n\n\n<p>Discover everything that was covered by <a href=\"https://wordpress.tv/2022/12/16/matt-mullenweg-state-of-the-word-2022/\">watching the official event recording</a> and join the ongoing #StateOfTheWord conversation on <a href=\"https://www.tumblr.com/tagged/StateoftheWord?sort=top\">Tumblr</a>, <a href=\"https://www.instagram.com/explore/tags/stateoftheword/\">Instagram</a>, <a href=\"https://www.facebook.com/hashtag/stateoftheword\">Facebook</a>, <a href=\"https://www.linkedin.com/feed/hashtag/?keywords=stateoftheword\">Linkedin</a>, and <a href=\"https://twitter.com/hashtag/StateOfTheWord\">Twitter</a>. For another way to get involved, consider sharing your experience with WordPress in the <a href=\"https://wordpress.org/news/2022/12/2022-wordpress-survey/\">2022 WordPress Community Survey</a>.</p>\n\n\n\n<span id=\"more-14110\"></span>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>Referenced Resources</strong>&nbsp;</h2>\n\n\n\n<ul>\n<li><a href=\"http://wordpress.tumblr.com/\">WordPress on Tumblr</a></li>\n\n\n\n<li><a href=\"http://engineawesome.com/\">Engineawesome.com</a> using Gutenberg</li>\n\n\n\n<li><a href=\"https://communitysummit.wordcamp.org/2023/\">WordPress Community Summit 2023</a></li>\n\n\n\n<li><a href=\"http://wp20.wordpress.net/\">WP20.wordpress.net</a></li>\n\n\n\n<li><a href=\"https://learn.wordpress.org/\">learn.WordPress.org</a></li>\n\n\n\n<li><a href=\"https://wordpress.org/openverse\">Openverse</a></li>\n\n\n\n<li><a href=\"https://wordpress.org/plugins/create-block-theme/\">Create Block Theme plugin</a></li>\n\n\n\n<li><a href=\"https://make.wordpress.org/\">Make.WordPress.org</a></li>\n\n\n\n<li><a href=\"https://developer.wordpress.org/playground\">WordPress Playground</a></li>\n\n\n\n<li><a href=\"http://photomatt.tumblr.com/\">Matt on Tumblr</a></li>\n\n\n\n<li><a href=\"https://distributed.blog/\">Distributed.blog</a></li>\n</ul>\n\n\n\n<p><em><em>Special thanks to <a href=\'https://profiles.wordpress.org/laurlittle/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>laurlittle</a> and <a href=\'https://profiles.wordpress.org/eidolonnight/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>eidolonnight</a> for review and collaboration.</em></em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14110\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:5;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:56:\"Share Your Experience: The 2022 WordPress Survey is Open\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:57:\"https://wordpress.org/news/2022/12/2022-wordpress-survey/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Thu, 01 Dec 2022 16:00:19 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:7:\"General\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:6:\"survey\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=14062\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:131:\"The 2022 WordPress survey is open for your input and available in English, French, German, Italian, Japanese, Russian, and Spanish.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:15:\"Chloe Bringmann\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:4584:\"\n<p>Each year, members of the WordPress community (users, site builders, extenders, and contributors) provide valuable feedback through an annual survey. Key takeaways and trends that emerge from this survey often find their way into the annual <a href=\"https://wordpress.org/news/2022/11/state-of-the-word-2022/\">State of the Word</a> address, are shared in the public project blogs, and can influence the direction and strategy for the WordPress project.</p>\n\n\n\n<p>Simply put: this survey helps those who build WordPress understand more about how the software is used, and by whom. The survey also helps leaders in the WordPress open source project learn more about our contributors’ experiences.&nbsp;&nbsp;</p>\n\n\n\n<p>To ensure that your WordPress experience is represented in the 2022 survey results, <a href=\"https://wordpressdotorg.survey.fm/en-2022\">take the 2022 annual survey now.</a></p>\n\n\n\n<div class=\"wp-block-buttons is-layout-flex\">\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link wp-element-button\" href=\"https://wordpressdotorg.survey.fm/en-2022\">Take the 2022 Survey (English)</a></div>\n</div>\n\n\n\n<p>You may also take the survey in <a href=\"https://wordpressdotorg.survey.fm/fr-2022\">French</a>, <a href=\"https://wordpressdotorg.survey.fm/de-2022\">German</a>, <a href=\"https://wordpressdotorg.survey.fm/it-2022\">Italian</a>, <a href=\"https://wordpressdotorg.survey.fm/jp-2022\">Japanese</a>, <a href=\"https://wordpressdotorg.survey.fm/ru-2022\">Russian</a>, or <a href=\"https://wordpressdotorg.survey.fm/es-2022\">Spanish</a>, thanks to the efforts of WordPress polyglot contributors. These are the most frequently installed languages based on the number of WordPress downloads. </p>\n\n\n\n<p>The survey will be open through the end of 2022, and then WordPress plans to publish the results sometime in 2023. This year, the survey questions have been refreshed for more effortless survey flow, completion, and analysis. Some questions have been removed, while a few new ones are now present, reflecting the present and future of WordPress. If you’re looking for the analysis of the 2021 survey results, those will also be shared in early 2023.</p>\n\n\n\n<h2 class=\"wp-block-heading\">Spread the word</h2>\n\n\n\n<p>Help spread the word about the survey by sharing it with your network, through Slack, or within your social media accounts. The more people who complete the survey and share their experience with WordPress, the more the project as a whole will benefit in the future.</p>\n\n\n\n<h2 class=\"wp-block-heading\">Security and privacy</h2>\n\n\n\n<p>Data security and privacy are paramount to the WordPress project and community. With this in mind, all data will be anonymized: no email addresses nor IP addresses will be associated with published results. To learn more about WordPress.org’s privacy practices, view the <a href=\"https://wordpress.org/about/privacy/\">privacy policy</a>.</p>\n\n\n\n<h2 class=\"wp-block-heading\">Thank you</h2>\n\n\n\n<p>Thank you to the following WordPress contributors for assisting with the annual survey project, including question creation, strategy, survey build-out, and translation:</p>\n\n\n\n<p><a href=\"https://profiles.wordpress.org/dansoschin/\">dansoschin</a>, <a href=\"https://profiles.wordpress.org/_dorsvenabili/\">_dorsvenabili</a>, <a href=\"https://profiles.wordpress.org/angelasjin/\">angelasjin</a>, <a href=\"https://profiles.wordpress.org/arkangel/\">arkangel</a>, <a href=\"https://profiles.wordpress.org/audrasjb/\">audrasjb</a>, <a href=\"https://profiles.wordpress.org/atachibana/\">atachibana</a>, <a href=\"https://profiles.wordpress.org/bjmcsherry/\">bjmcsherry</a>, <a href=\"https://profiles.wordpress.org/chanthaboune/\">chanthaboune</a>, <a href=\"https://profiles.wordpress.org/eidolonnight/\">eidolonnight</a>, <a href=\"https://profiles.wordpress.org/fernandot/\">fernandot</a>, <a href=\"https://profiles.wordpress.org/fierevere/\">fierevere</a>, <a href=\"https://profiles.wordpress.org/fxbenard/\">fxbenard</a>, <a href=\"https://profiles.wordpress.org/jdy68/\">jdy68</a>, <a href=\"https://profiles.wordpress.org/jpantani/\">jpantani</a>, <a href=\"https://profiles.wordpress.org/laurlittle/\">laurlittle</a>, <a href=\"https://profiles.wordpress.org/nao/\">nao</a>, <a href=\"https://profiles.wordpress.org/nielslange/\">nielslange</a>, <a href=\"https://profiles.wordpress.org/peiraisotta/\">peiraisotta</a>, <a href=\"https://profiles.wordpress.org/piermario/\">piermario</a>, <a href=\"https://profiles.wordpress.org/rmartinezduque/\">rmartinezduque</a>, <a href=\"https://profiles.wordpress.org/santanainniss/\">santanainniss</a>.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"14062\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:6;a:6:{s:4:\"data\";s:72:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:34:\"People of WordPress: Huanyi Chuang\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:69:\"https://wordpress.org/news/2022/11/people-of-wordpress-huanyi-chuang/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Wed, 30 Nov 2022 20:09:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:6:{i:0;a:5:{s:4:\"data\";s:9:\"Community\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:8:\"Features\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:7:\"General\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:3;a:5:{s:4:\"data\";s:10:\"Interviews\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:4;a:5:{s:4:\"data\";s:9:\"HeroPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:5;a:5:{s:4:\"data\";s:19:\"People of WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=13562\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:144:\"The latest People of WordPress story features Huanyi Chuang, from #Taiwan, on his journey to become a digital marketer and front end developer. \";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:11:\"Abha Thakor\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:11689:\"\n<p><strong>This month we feature Huanyi (Eric) Chuang, a front end developer from Taiwan, who helps connect local groups to WordPress and the worldwide open source community. He is part of the team helping to make the first WordCamp Asia a success in 2023.</strong></p>\n\n\n\n<p><strong>The People of WordPress series shares some of the inspiring stories of how people&#8217;s lives can change for the better through WordPress and its global network of contributors.</strong></p>\n\n\n\n<figure class=\"wp-block-image size-full\"><img decoding=\"async\" loading=\"lazy\" width=\"1014\" height=\"627\" src=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-featured-img.jpg?resize=1014%2C627&#038;ssl=1\" alt=\"Huanyi pictured sitting inside a rock formation.\" class=\"wp-image-13479\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-featured-img.jpg?w=1014&amp;ssl=1 1014w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-featured-img.jpg?w=300&amp;ssl=1 300w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-featured-img.jpg?w=768&amp;ssl=1 768w\" sizes=\"(max-width: 1000px) 100vw, 1000px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>Discovering WordPress and the benefit of child themes </strong></h2>\n\n\n\n<p>Huanyi’s first footsteps in WordPress began in 2017 when he worked for a firm that built blogs and developed ad content for clients.</p>\n\n\n\n<p>After building a few sites using the platform, he discovered child themes and through them opened up a world of possibilities for his clients. To this day, he uses child themes to deliver truly custom designs and functionality for clients.</p>\n\n\n\n<p>Later in his career, Huanyi moved into digital marketing, integrating sites with massive ad platforms like Google and Facebook. This led him to learn to work with tracking code and JavaScript. He also began his learning journey in HTML, CSS, and PHP, to be able to improve his development skills and customize child themes.</p>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>Meetups bring together software users to learn together</strong></h2>\n\n\n\n<figure class=\"wp-block-image size-full\"><img decoding=\"async\" loading=\"lazy\" width=\"600\" height=\"800\" src=\"https://i2.wp.com/wordpress.org/news/files/2022/09/huanyi-1.jpg?fit=600%2C800&amp;ssl=1\" alt=\"Huanyi and a koala.\" class=\"wp-image-13476\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-1.jpg?w=600&amp;ssl=1 600w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-1.jpg?w=225&amp;ssl=1 225w\" sizes=\"(max-width: 600px) 100vw, 600px\" /><figcaption class=\"wp-element-caption\"><em>Huanyi pictured in Australia during one of his travels meeting a koala bear.</em></figcaption></figure>\n\n\n\n<p>When Huanyi had a problem with a client&#8217;s site, he looked to WordPress meetups near where he lived in <a href=\"https://www.meetup.com/Taipei-WordPress/\">Taipei</a> to help find the solutions.</p>\n\n\n\n<blockquote class=\"wp-block-quote\">\n<p><strong>“When I encountered an issue with the custom archive pages, a local meetup announcement showed up on my WordPress dashboard</strong>.&#8221;</p>\n<cite>Huanyi Chuang</cite></blockquote>\n\n\n\n<p>At the meetup, he met more experienced WordPress users and developers there, who answered his questions and helped him learn.</p>\n\n\n\n<p>“When I encountered an issue with the custom archive pages, a local meetup announcement showed up on my WordPress dashboard. That was my original connection with the local community,” Huanyi said.</p>\n\n\n\n<p>The WordPress community gave Huanyi a chance to connect with people, feed his curiosity about the software, and join a circle of people he could share this interest.</p>\n\n\n\n<p>At first, he thought meetups were an opportunity to source new clients, and he took his business cards to every event. However, he soon found that these events offered him the opportunity to make friends and share knowledge.</p>\n\n\n\n<p>From then on, Huanyi started focusing more on what he could give to these events and networks, making new friends, and listening to people. This led him to share as a meetup speaker his own commercial website management experience.</p>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>The road to WordCamp</strong></h2>\n\n\n\n<p>It was going to his first meetup and then getting involved with WordCamps that changed Huanyi&#8217;s whole relationship with WordPress.</p>\n\n\n\n<figure class=\"wp-block-image size-full\"><img decoding=\"async\" loading=\"lazy\" width=\"600\" height=\"800\" src=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-2.jpg?resize=600%2C800&#038;ssl=1\" alt=\"Huanyi pictured on an outing, stood next to a white car.\" class=\"wp-image-13477\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-2.jpg?w=600&amp;ssl=1 600w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-2.jpg?w=225&amp;ssl=1 225w\" sizes=\"(max-width: 600px) 100vw, 600px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>In 2018, he took the step to help as an organizer, having joined the Taoyuan Meetup in Taiwan. He played several parts across the organizing team, and the welcoming feeling he got in every situation encouraged him to get more involved.</p>\n\n\n\n<p>He recalls meeting new friends from different fields and other countries, which gave him a great sense of achievement and strengthened his passion for participating in the community.</p>\n\n\n\n<p>When the team started this meetup, numbers were much lower than in the group in the city of Taipei, but they were not disheartened and gradually grew the local WordPress community.</p>\n\n\n\n<p>They created a pattern of ‘multiple organizers,’ which spread the workload and grew friendships.&nbsp;</p>\n\n\n\n<blockquote class=\"wp-block-quote\">\n<p><strong>“Being connected to and from meetups is the most valuable part of the community. Having these friends makes me gather more information. We share information and benefit from others’ information, and thus we gain more trust in each other. With such credibility, we share more deeply and build deeper relations.”</strong> </p>\n<cite>Huanyi Chuang</cite></blockquote>\n\n\n\n<p>Before the pandemic, the meetup met every month and grew to become the second largest meetup in Taiwan. Huanyi also contributed to the WordPress community as an organizer of WordCamp Taipei 2018 in the speaker team and lead organizer of <a href=\"https://taiwan.wordcamp.org/2021/\">WordCamp Taiwan 2021</a>.</p>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>So why should you join the community?</strong></h2>\n\n\n\n<p>According to Huanyi, you will always have something to take home with you. It might be new information or experiences. It might be plugins or theme ideas. But most of all, it is the chance to meet fascinating people and make new friends.</p>\n\n\n\n<blockquote class=\"wp-block-quote\">\n<p>Huanyi’s message to other contributors:<br>&#8220;Keep participating, and you will find more you can achieve than you expect.” </p>\n</blockquote>\n\n\n\n<p>He added that long-term participation will &#8216;let you feel the humanity behind the project&#8217;.</p>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>Localize: the road ahead for WordPress</strong></h2>\n\n\n\n<figure class=\"wp-block-image size-full\"><img decoding=\"async\" loading=\"lazy\" width=\"900\" height=\"675\" src=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-3.jpg?resize=900%2C675&#038;ssl=1\" alt=\"Huanyi standing on a sandy beach.\" class=\"wp-image-13478\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-3.jpg?w=900&amp;ssl=1 900w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-3.jpg?w=300&amp;ssl=1 300w, https://i0.wp.com/wordpress.org/news/files/2022/09/huanyi-3.jpg?w=768&amp;ssl=1 768w\" sizes=\"(max-width: 900px) 100vw, 900px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p>Huanyi believes WordPress has the power to break down the barriers between designers, project managers, developers, marketers, writers, and publishers. In Taiwan, he said WordPress is &#8216;a common protocol&#8217; that lets people from all of these disciplines work and communicate together more easily than they ever have before.</p>\n\n\n\n<p>That is why he works on and encourages others to localize plugins today. He believes localization of the software is the foundation for the extension of the WordPress community as it enables people to ‘Flex their Freedom’ in a language they speak!</p>\n\n\n\n<p>He has helped to organize online events around previous <a href=\"https://wptranslationday.org/\">WordPress Translation Day</a> events.</p>\n\n\n\n<p>Huanyi said: “I think it&#8217;s important to localize WordPress because its very concept of ‘open source’ means that people can access it freely. In another way, free from the monopoly of knowledge and speech. To achieve it, it&#8217;s important that people can access it with their own language.</p>\n\n\n\n<p>“Localization is the foundation of the extension of WordPress community because it helps people using different languages to access the project and lowers the hurdle to understand how things work.”</p>\n\n\n\n<h2 class=\"wp-block-heading\">Share the stories</h2>\n\n\n\n<p>Help share these stories of open source contributors and continue to grow the community. Meet more WordPressers in the <a href=\"https://wordpress.org/news/category/newsletter/interviews/\">People of WordPress series</a>.</p>\n\n\n\n<h2 class=\"wp-block-heading\"><strong>Contributors</strong></h2>\n\n\n\n<p>Thank you to <a href=\'https://profiles.wordpress.org/no249a002/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>no249a002</a> for sharing his adventures in WordPress.</p>\n\n\n\n<p>Thank you to Abha Thakor (<a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>), Mary Baum (<a href=\'https://profiles.wordpress.org/marybaum/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>marybaum</a>), Meher Bala (<a href=\'https://profiles.wordpress.org/meher/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>meher</a>), Chloe Bringmann (<a href=\'https://profiles.wordpress.org/cbringmann/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>cbringmann</a>), Surendra Thakor (<a href=\'https://profiles.wordpress.org/sthakor/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>sthakor</a>), Adeeb Malik (<a href=\'https://profiles.wordpress.org/adeebmalik/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>adeebmalik</a>) for research, interviews, and contributing to this feature article.</p>\n\n\n\n<p>The <em>People of WordPress</em> series thanks Josepha Haden (<a href=\'https://profiles.wordpress.org/chanthaboune/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>chanthaboune</a>) and Topher DeRosia (<a href=\'https://profiles.wordpress.org/topher1kenobe/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>topher1kenobe</a>) for their support.</p>\n\n\n\n<div class=\"wp-block-media-text is-stacked-on-mobile is-vertically-aligned-center\" style=\"grid-template-columns:29% auto\"><figure class=\"wp-block-media-text__media\"><img decoding=\"async\" loading=\"lazy\" width=\"180\" height=\"135\" src=\"https://i0.wp.com/wordpress.org/news/files/2020/03/heropress_logo_180.png?resize=180%2C135&#038;ssl=1\" alt=\"HeroPress logo\" class=\"wp-image-8409 size-full\" data-recalc-dims=\"1\" /></figure><div class=\"wp-block-media-text__content\">\n<p class=\"has-small-font-size\"><em>This People of WordPress feature is inspired by an essay originally published on </em><a href=\"https://heropress.com/\"><em>HeroPress.com</em></a><em>, a community initiative created by Topher DeRosia. It highlights people in the WordPress community who have overcome barriers and whose stories might otherwise go unheard. </em>#HeroPress </p>\n</div></div>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"13562\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:7;a:6:{s:4:\"data\";s:61:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:7:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:73:\"WP Briefing: Episode 44: Minors, Majors, and Why We Have So Many Releases\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:93:\"https://wordpress.org/news/2022/11/episode-44-minors-majors-and-why-we-have-so-many-releases/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 28 Nov 2022 12:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:7:\"Podcast\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:11:\"wp-briefing\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:53:\"https://wordpress.org/news/?post_type=podcast&p=13996\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:129:\"Hear Josepha Haden Chomphosy highlight the role of major and minor releases in the WordPress open source project in this episode.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:9:\"enclosure\";a:1:{i:0;a:5:{s:4:\"data\";s:0:\"\";s:7:\"attribs\";a:1:{s:0:\"\";a:3:{s:3:\"url\";s:60:\"https://wordpress.org/news/files/2022/11/WP-Briefing-044.mp3\";s:6:\"length\";s:1:\"0\";s:4:\"type\";s:0:\"\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"Santana Inniss\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:8637:\"\n<p>In the forty-fourth episode of the WordPress Briefing, our host Josepha Haden Chomphosy highlights the role of major and minor releases in the WordPress open source project. </p>\n\n\n\n<p><em><strong>Have a question you&#8217;d like answered? You can submit them to <a href=\"mailto:wpbriefing@wordpress.org\">wpbriefing@wordpress.org</a>, either written or as a voice recording.</strong></em></p>\n\n\n\n<h2 class=\"wp-block-heading\">Credits</h2>\n\n\n\n<p>Editor:&nbsp;<a href=\"https://profiles.wordpress.org/dustinhartzler/\">Dustin Hartzler</a><br>Logo:&nbsp;<a href=\"https://profiles.wordpress.org/javiarce/\" data-type=\"URL\" data-id=\"https://profiles.wordpress.org/javiarce/\">Javier Arce</a><br>Production:&nbsp;<a href=\"https://profiles.wordpress.org/santanainniss/\">Santana Inniss</a><br>Song: Fearless First by Kevin MacLeod </p>\n\n\n\n<h2 class=\"wp-block-heading\">References</h2>\n\n\n\n<p><a href=\"https://en.wikipedia.org/wiki/12th_man_(football)\" data-type=\"URL\" data-id=\"https://en.wikipedia.org/wiki/12th_man_(football)\">Twelfth Man</a><br><a href=\"https://wordpress.org/news/2022/11/state-of-the-word-2022/\" data-type=\"URL\" data-id=\"https://wordpress.org/news/2022/11/state-of-the-word-2022/\">State of the Word</a></p>\n\n\n\n<h2 class=\"wp-block-heading\">Transcript</h2>\n\n\n\n<span id=\"more-13996\"></span>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:00]&nbsp;</strong></p>\n\n\n\n<p>Hello everyone! And welcome to the WordPress Briefing, the podcast where you can catch quick explanations of the ideas behind the WordPress open source project, some insight into the community that supports it, and get a small list of big things coming up in the next two weeks.&nbsp;</p>\n\n\n\n<p>I&#8217;m your host, Josepha Haden Chomphosy. Here we go.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:00:27]&nbsp;</strong></p>\n\n\n\n<p>At the top of November, a new major release for WordPress shipped. That was WordPress 6.1. I know I talked about it basically nonstop. Then two weeks later, there was a new minor release for WordPress. It was WordPress 6.1.1, which I did not talk about at all. Way back in episode four of this podcast, I dug into the overall release cycle and what someone could expect from a high-level logistics aspect.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:01:05]&nbsp;</strong></p>\n\n\n\n<p>And today we&#8217;re gonna take a quick look at minor releases in particular. Just as a general heads up, I always want to lean into sports metaphors when I&#8217;m talking about releases, and I think it&#8217;s because of the words major and minor. And so, I&#8217;ve done my level best to not include that in any of my explanations today.</p>\n\n\n\n<p>But I do have one, I do have one that&#8217;s a sports thing. So just if you don&#8217;t like sports, know that it&#8217;s just one little bit and we&#8217;ll try to be carefully quick around it together. All right, so minor releases. You may have noticed that I don&#8217;t mention minor releases nearly as often as I mention major releases. And yet, most of the time, when we have a major release of WordPress, there&#8217;s a minor release that gets started almost immediately after.</p>\n\n\n\n<p>So first major versus minor. Major releases in WordPress happen roughly three times a year, give or take a release. Inside a major release, you will find that we include features, so– brand new abilities, enhancements, which you can generally call improvements to existing abilities, and also any bug fix that we can find, big or small, we&#8217;ll take &#8217;em all.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:02:16]&nbsp;</strong></p>\n\n\n\n<p>So minor releases in WordPress happen about four or five times a year on average. Minor releases include patches for issues introduced in the major release and any bug fix that doesn&#8217;t add or change functionality.&nbsp;</p>\n\n\n\n<p>If you&#8217;re with me so far, you probably have noted that there&#8217;s basically always at least one minor release per major release. And you might have also noted that I said minors include patches for issues we introduced in a major.</p>\n\n\n\n<p>Now, if I were hearing this with fresh ears, the first thing I would wonder is, okay, so if you start working on the minor right after you release the major to deal with issues you know you introduced in the major– why just not ship the major while there are bugs in it? Great question. I&#8217;m glad you asked. So there are a few things worth knowing here.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:03:09]&nbsp;</strong></p>\n\n\n\n<p>Firstly, there is this concept of “ship and iterate,” which is present in both agile and open source. The idea is that we ship software as soon as we have confidence that what is in the release is non-breaking and represents our best guests at a better experience for our users.</p>\n\n\n\n<p>Once that is out there, we use feedback on the initial release to quickly iterate and ship another release. That way, we don&#8217;t hold back any good features. And since we already planned the immediate minor, any major issues that show up can be fixed in as little as two weeks. Secondly, there is the concept that with many eyes, all bugs are shallow, which is primarily present in open source.</p>\n\n\n\n<p>The idea here is that with enough people looking at a problem, that problem doesn&#8217;t stand a chance. So when a release is shipped in a workable state but with interactions that could use some refinement, the fastest way to find those refinements is to take it to the community of WordPress users and developers and invite them to co-create this CMS with us.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:04:10]</strong></p>\n\n\n\n<p>Which touches on my final thought. The concept of the user as co-creator.</p>\n\n\n\n<p>If we think about the development and evolution of our software as a team effort, then we can think of the people who use our software as what&#8217;s called the “Twelfth Man” That&#8217;s in quotes, and I will, I&#8217;ll leave a link to that in the show notes as well.&nbsp;</p>\n\n\n\n<p>In sports, this refers to the fans. And if you&#8217;ve ever been to a live sporting event or played in any, you will know that the cheering and jeering from fans turns into this distinct motivating entity all its own. As a whole team or individual member, you know what you have to do. You know what you need to do in a game, but there&#8217;s something about that chaotic, loud roar of feedback that just brings life to what you&#8217;re doing, and that&#8217;s how I see our community of users.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:05:02]</strong></p>\n\n\n\n<p>So at the end of the day, the answer to the question of ‘why so many releases’ and the follow-up question of ‘why tolerate stable imperfection’ is largely the same. To get features into the hands of our users quickly so that we can always be breathing life into this CMS we care so much about.</p>\n\n\n\n<p>I hope that answers your questions about our release cadence, and if you didn&#8217;t come into this podcast having any questions about release cadences at all, I hope this new information brings a little extra light to the complexity of working in open source.&nbsp;</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:05:32]&nbsp;</strong></p>\n\n\n\n<p>That brings us now to our small list of big things.</p>\n\n\n\n<p>Big thing number one is that the State of the Word has been announced and is scheduled for December 15th. It&#8217;s a little earlier in the day than in past iterations, so I hope we get a new crew of listeners tuning in at the same time. I&#8217;ll leave a link to that in the show notes, or you can pop over to wordpress.org/news to see the announcement for yourself.</p>\n\n\n\n<p><strong>[Josepha Haden Chomphosy 00:06:00]&nbsp;</strong></p>\n\n\n\n<p>Big thing number two is that team rep nominations are open on most teams right now. So if organization and people wrangling are high on your list of ways you can give back to WordPress, head on over to the team you contribute to and see how you raise your hand for that.&nbsp;</p>\n\n\n\n<p>Then big thing number three is that big-picture goals, hopes, and timelines are being gathered, and I will ship those shortly after the start of the new year.</p>\n\n\n\n<p>It will give us all an idea of where we want to focus our attention to ensure that WordPress continues to grow toward the future. You can keep an eye out for that on make.wordpress.org/project.&nbsp;</p>\n\n\n\n<p>And that, my friends, is your small list of big things. Thanks for tuning in today for the WordPress Briefing. I&#8217;m your host, Josepha Haden Chomphosy, and I&#8217;ll see you again in a couple of weeks.</p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"13996\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:8;a:6:{s:4:\"data\";s:60:\"\n		\n		\n		\n		\n		\n				\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:39:\"The Month in WordPress – October 2022\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:71:\"https://wordpress.org/news/2022/11/the-month-in-wordpress-october-2022/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Tue, 22 Nov 2022 11:00:00 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:2:{i:0;a:5:{s:4:\"data\";s:18:\"Month in WordPress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:18:\"month in wordpress\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=13988\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:224:\"With the end of the year fast approaching, the WordPress project has not slowed down. Read on to learn more about the latest major release, WordPress 6.1, and the State of the Word 2022 live event, among other exciting news.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:14:\"rmartinezduque\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:14083:\"\n<p>With the end of the year fast approaching, the WordPress project has not slowed down. Read on to learn more about the latest major release, WordPress 6.1, and the State of the Word 2022 live event, among other exciting news. It&#8217;s time to catch up on all things WordPress!</p>\n\n\n\n<hr class=\"wp-block-separator has-css-opacity\" />\n\n\n\n<h2 class=\"wp-block-heading\">Say hello to WordPress 6.1 “Misha”</h2>\n\n\n\n<p>The third and last major release of 2022, <a href=\"https://wordpress.org/news/2022/11/misha/\"><strong>WordPress 6.1 “Misha,”</strong></a> shipped on November 1, 2022. Named after jazz pianist <strong>Mikhail “Misha” Alperin</strong>, this release comes packed with many improvements that refine the site-building experience introduced earlier this year in WordPress 5.9 and 6.0, as well as <a href=\"https://make.wordpress.org/core/2022/10/11/wordpress-6-1-accessibility-improvements/\">accessibility</a> and <a href=\"https://make.wordpress.org/core/2022/10/11/performance-field-guide-for-wordpress-6-1/\">performance</a> upgrades.</p>\n\n\n\n<p>WordPress 6.1 is also bundled with a new default block theme, <a href=\"https://wordpress.org/news/2022/11/introducing-twenty-twenty-three/\">Twenty Twenty-Three (TT3)</a>, that features <strong>10 style variations</strong> designed by WordPress community members. These intentionally unique designs ensure that you can change the visual details of your site with ease—and within a single theme.</p>\n\n\n\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<iframe loading=\"lazy\" class=\"youtube-player\" width=\"600\" height=\"338\" src=\"https://www.youtube.com/embed/1w9oywSa6Hw?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\" allowfullscreen=\"true\" style=\"border:0;\" sandbox=\"allow-scripts allow-same-origin allow-popups allow-presentation\"></iframe>\n</div></figure>\n\n\n\n<p>Learn more about what&#8217;s in 6.1:</p>\n\n\n\n<ul>\n<li><a href=\"https://wordpress.org/news/2022/11/misha/\">WordPress 6.1 “Misha” Announcement</a></li>\n\n\n\n<li><a href=\"https://make.wordpress.org/core/2022/10/12/wordpress-6-1-field-guide/\">WordPress 6.1 Field Guide</a></li>\n</ul>\n\n\n\n<p>Following WordPress 6.1 “Misha”, a <a href=\"https://wordpress.org/news/2022/11/wordpress-6-1-1-maintenance-release/\">6.1.1 maintenance release</a> landed on November 15, 2022. This minor release includes about 50 bug fixes.</p>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p><a href=\"https://wordpress.org/download/\">Download WordPress 6.1.1</a></p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">State of the Word 2022 is coming on December 15</h2>\n\n\n\n<figure class=\"wp-block-image size-large\"><img decoding=\"async\" loading=\"lazy\" width=\"1024\" height=\"683\" src=\"https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=1024%2C683&#038;ssl=1\" alt=\"Decorative blue background with text: &quot;State of the Word 2022. December 15, 2022. 1–2:30 P.M. EST (18–19:30 UTC.) New York City.&quot;\" class=\"wp-image-13956\" srcset=\"https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=1024%2C683&amp;ssl=1 1024w, https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=300%2C200&amp;ssl=1 300w, https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=768%2C512&amp;ssl=1 768w, https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=1536%2C1024&amp;ssl=1 1536w, https://i0.wp.com/wordpress.org/news/files/2022/11/Web-SOTW-Image-1200x800-1.png?resize=2048%2C1365&amp;ssl=1 2048w\" sizes=\"(max-width: 1000px) 100vw, 1000px\" data-recalc-dims=\"1\" /></figure>\n\n\n\n<p><strong>State of the Word 2022</strong>, the annual keynote address delivered by the WordPress project’s co-founder, Matt Mullenweg, will be held on <strong>December 15, 2022</strong>. The event will take place in person in New York City and live-streamed via various WordPress.org social media platforms.</p>\n\n\n\n<p>You can also host or join a <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/state-of-the-word-watch-parties/\">State of the Word watch party</a> to enjoy the event with your WordPress friends.</p>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p><a href=\"https://wordpress.org/news/2022/11/state-of-the-word-2022/\">Learn more about State of the Word 2022</a></p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">What’s new in Gutenberg</h2>\n\n\n\n<p>Two new versions of Gutenberg have shipped in the last month:</p>\n\n\n\n<ul>\n<li><a href=\"https://make.wordpress.org/core/2022/10/27/whats-new-in-gutenberg-14-4-26-october/\"><strong>Gutenberg 14.4</strong></a> was released on October 26, 2022, with support for a distraction-free mode that allows a more focused writing experience. Other notable highlights include a redesigned pattern inserter, content locking to the Navigation block, and improvements to fluid typography.</li>\n\n\n\n<li><a href=\"https://make.wordpress.org/core/2022/11/09/whats-new-in-gutenberg-14-5-9-november/\"><strong>Gutenberg 14.5</strong></a> sets the groundwork for future releases with code quality improvements and bug fixes. This version introduces a new “Document Overview” panel for easier access to the list view and document information, expands margin and padding support, and improves spacing visualizers. It was released on November 9, 2022.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Explore some of the latest enhancements to the writing experience in <a href=\"https://make.wordpress.org/core/2022/10/28/core-editor-improvement-advancing-the-writing-experience/\">this Core Editor Improvement post</a>.</p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">Team updates: Documentation Contributor Day, WordPress.org redesign updates, and more</h2>\n\n\n\n<ul>\n<li>After a <a href=\"https://make.wordpress.org/core/2022/07/27/giving-fse-a-more-user-friendly-name/\">lively discussion</a> around the site editing terminology, WordPress Executive Director Josepha Haden Chomphosy <a href=\"https://make.wordpress.org/updates/2022/11/04/site-editor-a-more-user-friendly-name/\">announced that the term &#8220;Site Editor&#8221; will be used going forward</a> in reference to the suite of site editing tools.</li>\n\n\n\n<li>Last month, the Docs Team successfully held its <a href=\"https://make.wordpress.org/docs/2022/10/28/the-documentation-team-contributor-day-summary/\">first online Contributor Day</a> to collaborate in real-time, help onboard new contributors, and work on backlog tasks.</li>\n\n\n\n<li>Progress and next steps for the WordPress.org website redesign were shared <a href=\"https://make.wordpress.org/meta/2022/10/21/wordpress-org-redesign-update/\">in this post</a>. The following pages that will get a refreshed look and feel soon include <a href=\"https://make.wordpress.org/design/2022/11/11/a-refresh-of-wordpress-org-showcase/\">Showcase</a> and <a href=\"https://make.wordpress.org/docs/2022/10/19/new-design-for-helphub-in-wordpress-org/\">Documentation (HelpHub)</a>.</li>\n\n\n\n<li>The WordPress.org Theme Directory <a href=\"https://make.wordpress.org/meta/2022/10/20/displaying-style-variations-for-supporting-themes/\">introduced a new feature</a> that allows visitors to preview style variations in block themes.</li>\n\n\n\n<li>Inspired by the Community Team’s efforts to reactivate meetup groups, the Polyglots Team is looking to start a <a href=\"https://make.wordpress.org/polyglots/2022/11/08/starting-a-polyglots-outreach-effort/\">Polyglots outreach project</a> and welcomes help.</li>\n\n\n\n<li>The Training Team shared an <a href=\"https://make.wordpress.org/training/2022/11/01/summary-update-courses-currently-in-development-1-november-2022/\">overview of some of the new Learn WordPress courses</a> they have been recently working on.</li>\n\n\n\n<li>Openverse’s catalog now includes <a href=\"https://make.wordpress.org/openverse/2022/11/16/openverse-now-includes-over-1-million-audio-records/\">more than one million audio records</a>!</li>\n\n\n\n<li>The WordPress Photo Directory also reached a significant milestone by surpassing 5,000 photos! <a href=\"https://make.wordpress.org/photos/2022/11/17/the-road-to-5000-photos-a-retrospective/\">Check out this celebratory retrospective from the Photos team</a>.</li>\n\n\n\n<li>Speaking of Openverse and the Photo Directory, <a href=\"https://wordpress.org/news/2022/11/episode-43-openverse-photo-directory-what-are-they-and-how-are-they-different/\">don’t miss the latest episode of the WP Briefing podcast</a>—it covers the differences between these two resources, and how they work to further openly-licensed media.</li>\n\n\n\n<li>The latest edition of People of WordPress features <a href=\"https://wordpress.org/news/2022/10/people-of-wordpress-raghavendra-satish-peri/\">Raghavendra Satish Peri</a>.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Enjoy a spooky Halloween Mad Libs story completed by community contributors in <a href=\"https://wordpress.org/news/2022/10/episode-42-something-spooky-this-way-comes/\">Episode 42 of WP Briefing</a>.</p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">Feedback &amp; testing requests</h2>\n\n\n\n<ul>\n<li>The <a href=\"https://make.wordpress.org/core/2022/10/12/call-for-testing-plugin-dependencies/\">Plugin Dependencies feature plugin is available for testing</a>. Provide your feedback by December 1, 2022.</li>\n\n\n\n<li>What does a genuinely sustainable WordPress community look like to you? Share your vision and thoughts in <a href=\"https://make.wordpress.org/project/2022/11/01/sustainability-channel-what-should-we-do/\">this discussion post</a>.</li>\n\n\n\n<li>The new WordPress developer blog is in public beta and <a href=\"https://make.wordpress.org/core/2022/11/21/wordpress-developer-blog-is-in-public-beta/\">ready for feedback</a>.</li>\n\n\n\n<li>The Core Team requests assistance with <a href=\"https://make.wordpress.org/hosting/2022/10/19/call-for-action-testing-rollback-feature/\">testing a new Rollback Feature in WordPress core</a>.</li>\n\n\n\n<li>Version 21.2 of the WordPress mobile app for <a href=\"https://make.wordpress.org/mobile/2022/11/15/call-for-testing-wordpress-for-android-21-2/\">Android</a> and <a href=\"https://make.wordpress.org/mobile/2022/11/14/call-for-testing-wordpress-for-ios-21-2/\">iOS</a> is available for testing.</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Were you involved in WordPress 6.1? <a href=\"https://make.wordpress.org/core/2022/11/09/wordpress-6-1-misha-retrospective/\">Share your thoughts</a> on the release process by December 15, 2022.</p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<h2 class=\"wp-block-heading\">Event updates &amp; WordCamps</h2>\n\n\n\n<ul>\n<li>The WordCamp Asia organizing team is collaborating with WordCamp Central to bring the <a href=\"https://asia.wordcamp.org/2023/apply-for-wordcamp-asia-diversity-scholarship/\">WordCamp Asia Diversity Scholarship</a>. In addition, the team recently announced the <a href=\"https://asia.wordcamp.org/2023/speakers-announcement-round-1/\">first round of speakers</a>.</li>\n\n\n\n<li>Preparations for <a href=\"https://europe.wordcamp.org/2023/the-preparations-for-wordcamp-europe-2023-have-begun/\">WordCamp Europe 2023</a> are underway.</li>\n\n\n\n<li>Don’t miss these upcoming WordCamps:\n<ul>\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1e7-1f1f7.png\" alt=\"🇧🇷\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://saopaulo.wordcamp.org/2022/\">WordCamp São Paulo</a>, Brazil on November 26, 2022</li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ee-1f1f3.png\" alt=\"🇮🇳\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://kolkata.wordcamp.org/2022/\">WordCamp Kolkata</a>, West Bengal, India on December 17-18, 2022</li>\n</ul>\n</li>\n</ul>\n\n\n\n<blockquote class=\"wp-block-quote has-extra-large-font-size\">\n<p>Boost your speaking confidence in WordPress events. Register for the <a href=\"https://www.eventbrite.com/e/how-to-own-your-expertise-start-speaking-at-wordpress-events-wpdiversity-tickets-462153532657\">How to Own Your Expertise &amp; Start Speaking at WordPress Events</a> online workshop happening December 7, 2022.</p>\n</blockquote>\n\n\n\n<div style=\"height:10px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n\n\n\n<hr class=\"wp-block-separator has-css-opacity\" />\n\n\n\n<p><strong><em><strong><em><strong><em>Have a story that we should include in the next issue of The Month in WordPress? <strong><em>Fill out </em></strong><a href=\"https://make.wordpress.org/community/month-in-wordpress-submissions/\"><strong><em>this quick form</em></strong></a><strong><em> to let us know.</em></strong></em></strong></em></strong></em></strong></p>\n\n\n\n<p><em><em>The following folks contributed to this edition of The Month in WordPress:</em> <a href=\'https://profiles.wordpress.org/rmartinezduque/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>rmartinezduque</a>, <a href=\'https://profiles.wordpress.org/webcommsat/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>webcommsat</a>, <a href=\'https://profiles.wordpress.org/santanainniss/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>santanainniss</a>, <a href=\'https://profiles.wordpress.org/dansoschin/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>dansoschin</a>, <a href=\'https://profiles.wordpress.org/eidolonnight/\' class=\'mention\'><span class=\'mentions-prefix\'>@</span>eidolonnight</a>.</em></p>\n\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:30:\"com-wordpress:feed-additions:1\";a:1:{s:7:\"post-id\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"13988\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:9;a:6:{s:4:\"data\";s:63:\"\n		\n		\n		\n		\n		\n				\n		\n		\n		\n\n					\n										\n					\n		\n		\n			\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:4:{s:0:\"\";a:6:{s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:22:\"State of the Word 2022\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:58:\"https://wordpress.org/news/2022/11/state-of-the-word-2022/\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:7:\"pubDate\";a:1:{i:0;a:5:{s:4:\"data\";s:31:\"Mon, 21 Nov 2022 17:14:43 +0000\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:8:\"category\";a:3:{i:0;a:5:{s:4:\"data\";s:6:\"Events\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:1;a:5:{s:4:\"data\";s:4:\"sotw\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}i:2;a:5:{s:4:\"data\";s:17:\"state of the word\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:35:\"https://wordpress.org/news/?p=13971\";s:7:\"attribs\";a:1:{s:0:\"\";a:1:{s:11:\"isPermaLink\";s:5:\"false\";}}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:11:\"description\";a:1:{i:0;a:5:{s:4:\"data\";s:223:\"Join the WordPress project leaders for State of the Word 2022 in New York City on December 15, 2022. Also live-streamed on social media, you will hear about the WordPress project, open source, and more in this annual event.\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:7:\"Josepha\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:40:\"http://purl.org/rss/1.0/modules/content/\";a:1:{s:7:\"encoded\";a:1:{i:0;a:5:{s:4:\"data\";s:18044:\"\n<figure class=\"wp-block-embed is-type-video is-provider-youtube wp-block-embed-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\n<iframe loading=\"lazy\" class=\"youtube-player\" width=\"600\" height=\"338\" src=\"https://www.youtube.com/embed/VeigCZuxnfY?version=3&#038;rel=1&#038;showsearch=0&#038;showinfo=1&#038;iv_load_policy=1&#038;fs=1&#038;hl=en-US&#038;autohide=2&#038;wmode=transparent\" allowfullscreen=\"true\" style=\"border:0;\" sandbox=\"allow-scripts allow-same-origin allow-popups allow-presentation\"></iframe>\n</div></figure>\n\n\n\n<p>Hello, WordPress!&nbsp;</p>\n\n\n\n<p><a href=\"https://calendar.google.com/calendar/u/0/r/eventedit?text=State+of+the+Word+2022&amp;dates=20221215T180000Z/20221215T193000Z&amp;details=State+of+the+Word+is+the+annual+keynote+address+delivered+by+the+WordPress+project%E2%80%99s+co-founder%2C+Matt+Mullenweg.+Every+year%2C+the+event+shares+reflections+on+the+project%E2%80%99s+progress+and+the+future+of+open+source.+Expect+this+and+more+in+this+year%E2%80%99s+edition.%0A%0AState+of+the+Word+will+be+live+streamed+from+New+York+City,+and+is+free+for+all+to+watch.%0A%0Ahttps://wordpress.org/news/2022/11/state-of-the-word-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">Mark your calendars</a>; it’s almost time for State of the Word 2022!</p>\n\n\n\n<p>State of the Word is the annual keynote address delivered by the WordPress project’s co-founder, <a href=\"https://ma.tt\">Matt Mullenweg</a>. Every year, the event shares reflections on the project’s progress and the future of open source. Expect this and more in this year’s edition.</p>\n\n\n\n<p>This year’s event will take place in person in New York City and live-streamed via various WordPress.org social media platforms.&nbsp;</p>\n\n\n\n<p>Join Matt as he provides a retrospective of 2022, the latest WordPress releases, Site Editor advancements, and a return to in-person events around the globe, among other topics.</p>\n\n\n\n<p><strong>What:</strong> State of the Word 2022</p>\n\n\n\n<p><strong>When:</strong> <a href=\"https://calendar.google.com/calendar/u/0/r/eventedit?text=State+of+the+Word+2022&amp;dates=20221215T180000Z/20221215T193000Z&amp;details=State+of+the+Word+is+the+annual+keynote+address+delivered+by+the+WordPress+project%E2%80%99s+co-founder%2C+Matt+Mullenweg.+Every+year%2C+the+event+shares+reflections+on+the+project%E2%80%99s+progress+and+the+future+of+open+source.+Expect+this+and+more+in+this+year%E2%80%99s+edition.%0A%0AState+of+the+Word+will+be+live+streamed+from+New+York+City,+and+is+free+for+all+to+watch.%0A%0Ahttps://wordpress.org/news/2022/11/state-of-the-word-2022/\" target=\"_blank\" rel=\"noreferrer noopener\">December 15, 2022, 1–2:30 P.M. EST (18–19:30 UTC)</a></p>\n\n\n\n<p><strong>How:</strong> If you’re watching from the comfort of your home or <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/state-of-the-word-watch-parties/\">local watch party</a>, the live stream will be embedded in this post and available through the <a href=\"https://youtube.com/wordpress\">WordPress YouTube channel</a>.</p>\n\n\n\n<p><em>Update December 5, 2022: All in-person seats to State of the Word have been claimed at this time. Please join us online or at a local watch party instead!  </em><s>Would you like to join the in-person audience? <a href=\"https://wordcampcentral.survey.fm/request-a-seat-sotw-2022\">Request a seat by completing this survey</a>.</s></p>\n\n\n\n<h2 class=\"wp-block-heading\">Have a question for Matt?</h2>\n\n\n\n<p>State of the Word will include a Q&amp;A session. If you want to participate, you can either send your question ahead of time to <strong>ask-matt@wordcamp.org</strong> or ask during the event in the live stream chat on YouTube.</p>\n\n\n\n<p>Given the volume of questions that are usually submitted, please note that it may not be possible to answer all of them in the live Q&amp;A. A follow-up post will be published after the State of the Word to answer those not covered at the event.</p>\n\n\n\n<p>First time attending State of the Word? Check out <a href=\"https://wordpress.tv/tag/state-of-the-word/\">previous years&#8217; recordings on</a> <a href=\"https://wordpress.tv/tag/state-of-the-word/\">WordPress.tv</a> to get a sense of the event.</p>\n\n\n\n<p>See you in person or online on December 15!</p>\n\n\n\n<hr class=\"wp-block-separator has-alpha-channel-opacity is-style-default\" />\n\n\n\n<h2 class=\"wp-block-heading\" id=\"join-a-state-of-the-word-watch-party-near-you\">Join a State of the Word Watch Party near You</h2>\n\n\n\n<p>Can’t make it to New York? No problem, organize or join a watch party in your community in person or online. Like last year, the Community team has resources available to help! Check out <a href=\"https://make.wordpress.org/community/handbook/meetup-organizer/state-of-the-word-watch-parties/\">this handbook page</a>, which includes event templates, information on requesting a Zoom account, and how to get some swag.</p>\n\n\n\n<p>Gather together to look back on how WordPress has grown in 2022 and what is ahead for 2023. Stay up-to-date as a group on the latest happenings in the WordPress world and collaborate together on any questions you might have for Matt!</p>\n\n\n\n<p>Below you can find a list of State of the Word watch parties that will be updated regularly as the event approaches. If you don’t see a watch party in your region, <a href=\"https://meetup.com/pro/wordpress\">check this page on Meetup.com</a> to see if your local WordPress group is organizing one.</p>\n\n\n\n<ul>\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ee-1f1e9.png\" alt=\"🇮🇩\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://www.meetup.com/bali-wordpress-meetup\">Bali</a> – <a href=\"https://www.meetup.com/bali-wordpress-meetup/events/290077585/\">Nobar Akbar – State of the Word 2022</a></li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ee-1f1e9.png\" alt=\"🇮🇩\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://www.meetup.com/Bandung-WordPress-Meetup\">Bandung</a> &#8211; <a href=\"https://www.meetup.com/Bandung-WordPress-Meetup/events/290189476/\">Nonton bareng “State of the Word 2022”</a></li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ee-1f1e9.png\" alt=\"🇮🇩\" class=\"wp-smiley\" style=\"height: 1em; max-height: 1em;\" /> <a href=\"https://www.meetup.com/demak-wordpress-meetup\">Demak</a> &#8211; <a href=\"https://www.meetup.com/demak-wordpress-meetup/events/290139514/\">Nobar Akbar &#8211; State of the Word 2022</a></li>\n\n\n\n<li><img src=\"https://s.w.org/images/core/emoji/14.0.0/72x72/1f1ee-1f1e9.png\" alt=\"🇮🇩\" class=\"wp-smiley\" st